import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import './Form.css';

const MyForm = () => {
  const [formData, setFormData] = useState({
    url: '',
    type: '',
    time: '',
    periodo: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aqui você pode enviar os dados para o banco de dados MySQL
    console.log(formData);
  };

  return (
    <div className="form-container">
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="url">
          <Form.Label className="form-label">URL</Form.Label>
          <Form.Control
            type="text"
            name="url"
            value={formData.url}
            onChange={handleChange}
            placeholder="Digite a URL"
            required
            className="form-control"
          />
        </Form.Group>

        <Form.Group controlId="type">
          <Form.Label className="form-label">Tipo</Form.Label>
          <Form.Control
            type="text"
            name="type"
            value={formData.type}
            onChange={handleChange}
            placeholder="Digite o tipo"
            required
            className="form-control"
          />
        </Form.Group>

        <Form.Group controlId="time">
          <Form.Label className="form-label">Tempo</Form.Label>
          <Form.Control
            type="text"
            name="time"
            value={formData.time}
            onChange={handleChange}
            placeholder="Digite o tempo"
            required
            className="form-control"
          />
        </Form.Group>

        <Form.Group controlId="periodo">
          <Form.Label className="form-label">Período</Form.Label>
          <Form.Control
            type="text"
            name="periodo"
            value={formData.periodo}
            onChange={handleChange}
            placeholder="Digite o período"
            required
            className="form-control"
          />
        </Form.Group>

        <Button variant="primary" type="submit" className="submit-button">
          Enviar
        </Button>
      </Form>
    </div>
  );
};

export default MyForm;
