    {
        id: 1,
        urlImage: "https://images.unsplash.com/photo-1488590528505-98d2b5aba04b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
        alt: "Image 1"

    },
    {
        id: 2,
        urlImage: "https://images.unsplash.com/photo-1531297484001-80022131f5a1?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1120&q=80",
        alt: "Image 2",
        caption: "image 1 teste",
        description: "image 1 teste"
    }
    
    
]


function Slide() {
  return (
    <Carousel>
      {imgs.map((img) =>(

        <Carousel.Item Key={imgs.id} interval={1000} className='Img'>
            <img
                className="d-block w-100"
                src={img.urlImage}
                alt={img.alt}
            />
            <Carousel.Caption>
                <h3>{img.caption}</h3>
                <p>{img.description}</p>
            </Carousel.Caption>
        </Carousel.Item>

      ))}
