import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import "./Slide.css"

import imgs from './app.json'


function Slide() {
  return (
    <Carousel>
       {imgs.map((img) => {
        if (img.type) {
           return <Carousel.Item Key={imgs.id} interval={img.time} className='Img'>
            <video className="w-100" autoPlay loop muted>
              <source
                src={img.url}
                type={img.type} 
                allowFullScreen
              />
            </video>;
            </Carousel.Item>
          
        }else
          return <Carousel.Item Key={imgs.id} interval={1000} className='Img'>

            <img src={img.url} alt="" className='w-100' />
            <iframe className="d-block w-100 h-100" src={img.url} title="description"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; 
              picture-in-picture; web-share; autoplay" sandbox='sandbox'
              referrerPolicy='no-referrer'
              allowfullscreen
            ></iframe>
            <iframe src="https://www.linkedin.com/embed/feed/update/urn:li:share:7058818059456667648" height="641" width="504" frameborder="0" allowfullscreen="" title="Publicação incorporada"></iframe>

        </Carousel.Item>
      

       
      })}
    </Carousel>
   
   
   
   
    
  );
}

export default Slide;
