import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

const form = document.querySelector('form');
const urlInput = document.querySelector('#url');

form.addEventListener('submit', (event) => {
  event.preventDefault(); // Impede o envio padrão do formulário

  // Cria um objeto com o valor do campo de entrada
  const data = {
    url: urlInput.value,
  };

  // Converte o objeto em uma string JSON
  const jsonData = JSON.stringify(data);

  // Armazena a string JSON na sessionStorage
  sessionStorage.setItem('data', jsonData);

  // Limpa o campo de entrada do formulário
  urlInput.value = '';
});

function BasicExample() {
  return (
    <Form className='form'>
      <Form.Group className="mb-3" controlId="url" id='url'>
        <Form.Label>URL</Form.Label>
        <Form.Control type="url" placeholder="Digite a URL" />
        <Form.Text className="text-muted">
        </Form.Text>
      </Form.Group>

     
      <Button variant="primary" type="submit">
        Salvar
      </Button>
    </Form>
  );
}

export default BasicExample;
