import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaEdit, FaTrash } from 'react-icons/fa';
import './ImageGallery.css';

function ImageGallery() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/imagens');
      setImages(response.data);
    } catch (error) {
      console.error('Erro ao buscar os dados:', error);
    }
  };

  const handleDeleteImage = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/imagens/:${id}`);
      fetchImages();
    } catch (error) {
      console.error('Erro ao excluir a imagem:', error);
    }
  };

  return (
    <div className="image-gallery">
      {images.map((image) => (
        <div key={image.id} className="image-card">
          {image.tipo ? (
            <video className="image" autoPlay loop muted>
              <source src={image.URL} type={image.tipo} allowFullScreen />
            </video>
          ) : (
            <img src={image.URL} alt="" className="image" />
          )}

          <div className="image-details">
            <p className="image-info">{image.datainicial} - {image.datafinal}</p>
            <div className="image-actions">
              <FaEdit className="edit-icon" />
              <FaTrash className="delete-icon" onClick={() => handleDeleteImage(image.id)} />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ImageGallery;
