import React, { useState, useEffect } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import axios from 'axios';
import "./Slide.css"

function Slide() {
  const [imgs, setImgs] = useState([]);

  useEffect(() => {
    const fetchImagens = async () => {
      try {
        const response = await axios.get('http://localhost:5000/imagens');
        setImgs(response.data);
      } catch (error) {
        console.error('Erro ao buscar os dados:', error);
      }
    };

    fetchImagens();
  }, []);

  return (
    <Carousel>
      {imgs.map((img, index) => {
        if (img.tipo) {
          return (
            <Carousel.Item key={index} interval={img.tempo} className='Img'>
              <video className="w-100" autoPlay loop muted>
                <source src={img.URL} type={img.tipo} allowFullScreen />
              </video>
            </Carousel.Item>
          );
        } else {
          return (
            <Carousel.Item key={index} interval={1000} className='Img'>
              <img src={img.URL} alt="" className='w-100' />
              <iframe className="d-block w-100 h-100" src={img.URL} title="description" allowFullScreen></iframe>
            </Carousel.Item>
          );
        }
      })}
    </Carousel>
  );
}

export default Slide;
