import React, { useEffect, useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import axios from 'axios';
import './Slide.css';

function Slide() {
  const [imgs, setImgs] = useState([]);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/imagens');
      setImgs(response.data);
    } catch (error) {
      console.error('Erro ao obter as imagens:', error);
    }
  };

  return (
    <Carousel>
      {imgs.map((img) => {
        if (img.type) {
          return (
            <Carousel.Item key={img.id} interval={img.time} className='Img'>
              <video className="w-100" autoPlay loop muted>
                <source src={img.url} type={img.type} allowFullScreen />
              </video>
            </Carousel.Item>
          );
        } else {
          return (
            <Carousel.Item key={img.id} interval={1000} className='Img'>
              <img src={img.url} alt="" className='w-100' />
              <iframe className="d-block w-100 h-100" src={img.url} title="description" allowFullScreen></iframe>
            </Carousel.Item>
          );
        }
      })}
    </Carousel>
  );
}

export default Slide;
