import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';
import "./Form.css"

const Formulario = () => {
  const [formData, setFormData] = useState({
    url: '',
    type: '',
    time: '',
    datainicial: '', 
    datafinal: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/enviar-dados', formData);
      console.log(response.data);
    } catch (error) {
      console.error('Erro ao enviar os dados:', error);
    }
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="url">
        <Form.Label>URL</Form.Label>
        <Form.Control
         type="url"
          name="url"
          value={formData.url}
          onChange={handleChange}
          placeholder="https://example.com"
          required
        />
      </Form.Group>

      <Form.Group controlId="type">
        <Form.Label>Tipo</Form.Label>
        <Form.Control
          type="text"
          name="type"
          value={formData.type}
          onChange={handleChange}
          placeholder='Se for video adicione "video/mp4" se não deixe em branco'
        />
      </Form.Group>

      <Form.Group controlId="time">
        <Form.Label>Tempo</Form.Label>
        <Form.Control
          type="time"
          name="time"
          value={formData.time}
          onChange={handleChange}
          placeholder="Digite o tempo que vai passar na tela"
          required
        />
      </Form.Group>

      <Form.Group controlId="datainicial">
          <Form.Label>Data Inicial</Form.Label>
          <Form.Control
            type="date"
            name="startDate"
            value={formData.datainicial}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="datafinaç">
          <Form.Label>Data Final</Form.Label>
          <Form.Control
            type="date"
            name="endDate"
            value={formData.datafinal}
            onChange={handleChange}
            required
          />
        </Form.Group>

      <Button variant="primary" type="submit">
        Enviar
      </Button>
    </Form>
  );
};

export default Formulario;
