import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import "./Slide.css"
import imgs from "./app.json"

function Slide() {
  return (
    <Carousel>
      {imgs.map((img) => (

        <Carousel.Item Key={imgs.id} interval={1000} className='Img'>
          <video className="w-100" autoPlay loop muted>
            <source
              src={img.urlVideo}
              type="video/mp4"
              allowFullScreen
            />
          </video>
          <video className="w-100" autoPlay loop muted>
            <source
              src={img.url}
              type={img.type}
              allowFullScreen
            />
          </video>


          {/* <img src={img.urlImage} alt="" className='w-100' />
          <iframe className="d-block w-100 h-100" src={img.urlSite} title="description"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; 
              picture-in-picture; web-share; autoplay" sandbox='sandbox'
            referrerPolicy='no-referrer'
            allowfullscreen
          ></iframe> */}

        </Carousel.Item>

      ))}
    </Carousel>
  );
}

export default Slide;
