import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import "./Slide.css"
import imgs from "./app.json"

function Slide() {
  return (
    <Carousel>
       {imgs.map((img) => {
        if (img.type) {
           <Carousel.Item Key={imgs.id} interval={10000} className='Img'>
            <video className="w-100" autoPlay loop muted>
              <source
                src={img.url}
                type={img.type} 
                allowFullScreen
              />
            </video>;
            </Carousel.Item>
          
        }

        return <h2 key={index}>X</h2>;
      })}
    </Carousel>
   
   
   
   
   
  );
}

export default Slide;
